import React from 'react'
import './App.css';
import Display from "./Components/Display";
import Buttons from "./Components/Buttons";
import Button from "./Components/Button/Button";
import ShowNumber from "./Components/ShowNumber/ShowNumber";

class App extends React.Component {

    state = {
        number: 0,
        buttons: [
            {buttonsTitle: '+', isDone: true},
            {buttonsTitle: '-', isDone: false},
        ],

    };



    render = () => {
        return (
            <div>
                <ShowNumber number={this.state.number}/>
                <Button onClickButton={''} buttonsTitle={''}/>
                <Buttons onClickButtonsPlusMinus={this.onClickButtonsPlusMinus} buttons={this.state.buttons}/>
            </div>
        );
    }
}

export default App;

