import React from 'react'
import './App.css';
import Display from "./Components/Display";
import Plus from "./Components/Plus";
import Minus from "./Components/Minus";
import Buttons from "./Components/Buttons";


class App extends React.Component {

    state = {
        number: 0,
        action: [
            {title: 'Plus', callback: this.sumNumber},
            {title: 'Minus', callback: this.minusNumber },
            {title: 'Drop', callback: this.dropNumber}
        ]
    };

    sumNumber = () => {
        let newNumber = this.state.number + 1;
        this.setState({number: newNumber })

    };

    minusNumber = () => {
        let newNumber = this.state.number - 1;
        this.setState({number: newNumber })

    };
    dropNumber = () => {
        let newNumber = this.state.number = 0;
        this.setState({number: newNumber })
    };



    render = () => {
        return (
                <div className='application'>
                    <Display number={this.state.number} />
                    <Buttons action={this.state.action} />
                    {/*<Plus sumNumber={this.sumNumber} />*/}
                    {/*<Minus minusNumber={this.minusNumber} />*/}
                </div>
        );
    }
}

export default App;
