import React from 'react'
import './App.css';
import Button from "./Components/Button/Button";
import ShowNumber from "./Components/ShowNumber/ShowNumber";

class App extends React.Component {

    state = {
        number: 0,
        buttons: [
            {buttonsTitle: '+'},
        ],
        limit: {limitTop: 6, errorText: 'Stop', errorBool: false},
    };

    onClickPlusNumber = () => {
        if (this.state.number === this.state.limit.limitTop - 1) this. setState({limit: {...this.state.limit, errorBool: true}})
            console.log();
        let newNumber = this.state.number;
        this.setState({number: newNumber + 1, limit: {...this.state.limit, errorBool: false}});
    };

    render = () => {
        return (
            <div>
                <ShowNumber limit={this.state.limit} number={this.state.number}/>
                <Button onClickButton={() => this.onClickPlusNumber()}
                        buttonsTitle={this.state.buttons[0].buttonsTitle}/>
            </div>
        );
    }
}

export default App;

