import React from 'react'
import css from './ShowNumber.module.css';

const ShowNumber = props => {
    let styleNumber = props.limit.errorEnd === true ? `${css.colorNumberEnd}` : `${css.colorNumberOk}`;
    let styleErrorStart = props.limit.errorColorStart === true ? `${css.colorErrorStartOrMax}` : ``;
    let styleErrorMax = props.limit.errorColorMax === true ? `${css.colorErrorStartOrMax}` : ``;
    return (
        <>
            {props.limit.errorColorStart &&
                <div className={`${styleErrorStart} ${styleErrorMax} ${styleNumber}`}>
                    {props.message}
                </div>
            }
            <div className={`${styleErrorStart} ${styleErrorMax} ${styleNumber}`}>
                {props.message}
            </div>
        </>
    );
};

export default ShowNumber;
